mapper.update:
update goods
set photo_url = #{photoUrl}
where id=#{id}

mapper.select:
select
from goods
and second_type_id=#{secondTypeId}
and name like '%' #{goodsName} '%'
and status_id=1
order by upload_date limit #{startIndex},#{pageSize}

select user_id
from goods
where id=#{goodsId}
